roller alloc] initWithRootViewController:[UserViewController new]] title:@"我" normalImage:@"person" selectedImage:@"person"];                 }/** *  设置 tabBarItem文本标题的颜色 */-(void)setTabBarItemTextAttributes {    // 设置普通状态下的文本颜色    NSMutableDictionary *normalAttribute = [NSMutableDictionary new];    normalAttribute[NSForegroundColorAttributeName] = [UIColor grayColor];    // 设置选中状态下的文本颜色    NSMutableDictionary *selectedAttibute = [NSMutableDictionary new];    selectedAttibute[NSForegroundColorAttributeName] = [UIColor redColor];    // 配置文本属性        UITabBarItem *tabBarItem = [UITabBarItem appearance];     [tabBarItem setTitleTextAttributes:normalAttribute forState:(UIControlStateNormal)];    [tabBarItem setTitleTextAttributes:selectedAttibute forState:(UIControlStateSelected)];        }/** *  给 tabbarController 添加一个子视图控制器 * *  @param viewController    子控制器 *  @param title             标题 *  @param nornameImageName  正常状态下的图片（名字） *  @param selectedImageName 选中状态下的图片（名字） */- (void)addOneChildViewController:(UIViewController *)viewController title:(NSString *)title normalImage:(NSString *)normalImageName selectedImage:(NSString *)selectedImageName {    viewController.view.backgroundColor = JT_RANDOM_COLOR;    viewController.tabBarItem.title = title;       viewController.tabBarItem.image = [[UIImage imageNamed:normalImageName]  imageWithRenderingMode:(UIImageRenderingModeAlwaysOriginal)];    viewController.tabBarItem.selectedImage = [[UIImage imageNamed:selectedImageName] imageWithRenderingMode:(UIImageRenderingModeAlwaysOriginal)];        [self  addChildViewController:viewController];}-(void)setCustomTabBar {        [self setValue:[[CustomTabBar   alloc] init] forKey:@"tabBar"];    }- (void)didReceiveMemoryWarning {    [super didReceiveMemoryWarning];    // Dispose of any resources that can be recreated.}/*#pragma mark - Navigation// In a storyboard-based application, you will often want to do a little preparation before navigation- (void)prepareForSegue:(UIStoryboardSegue *)segue sender:(id)sender {    // Get the new view controller using [segue destinationViewController].    // Pass the selected object to the new view controller.}*/@end